'use strict';

var pagination = require('hexo-pagination');

module.exports = function(locals){
    var config = this.config;
    var posts = locals.posts;

    // fix can't handle the case of sticky-post, thanks for: http://www.yuedongxu.cn/wod/
    posts.data = posts.data.sort(function(a, b) {
        if(a.sticky && b.sticky) {
            if(a.sticky == b.sticky) return b.updated - a.updated; // because of I want to sort posts with updated rather than created-date
            else return b.sticky - a.sticky;
        }
        else if(a.sticky && !b.sticky) {
            return -1;
        }
        else if(!a.sticky && b.sticky) {
            return 1;
        }
        else return b.updated - a.updated; // because of I want to sort posts with updated rather than created-date
    });

    var paginationDir = config.pagination_dir || 'page';

    return pagination('', posts, {
        perPage: config.index_generator.per_page,
        layout: ['index', 'archive'],
        format: paginationDir + '/%d/',
        data: {
            __index: true
        }
    });
};